package Chap03;

public class Example01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 20;
		System.out.println("x+y=" + (x + y)); // 모든 산술 연산에 괄호를 해줌(중요)
		System.out.println("x-y=" + (x - y)); // - 연산자
		System.out.println("x*y=" + (x * y)); // * 연산자
		System.out.println("x/y=" + (x / y)); // / 연산자
		System.out.println("x%y=" + (x % y)); // 나머지 연산자
	}

}
