package Chap03;

public class Example06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 100; // 정수 자료형
		long l = i; // 정수형 자료형
		float f = l; // 실수형 자료형
		System.out.println(i);
		System.out.println(l);
		System.out.println(f);// 실수형이기 때문에 .0을 동반하여 출력

	}

}
