package Chap03;

public class Operator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c;
		System.out.println(c = a); // c에 a를 대입
		System.out.println(b += a); // b=b+a
		System.out.println(b -= a); // b=b-a
		System.out.println(b *= a); // b=b*a
		System.out.println(b /= a); // b=b/a
		System.out.println(b %= a); // b=b%a

	}

}
