package Chap03;

public class TypeConversion01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		double y = 3.0;
		System.out.println(x + y); // double 실수형이기 때문에/더 큰 자료형이기도 함 .0출력
		System.out.println(x - y); // "
		System.out.println(x * y); // "
		System.out.println(x / y); // 실수형으로 표현해 주기 때문에 소수점 끝자까지 출력

	}

}
